    public static int shortestBridge(int[][] A) {
        int rows = A.length, cols = rows > 0 ? A[0].length : 0;
//        Change first island numbers to -1;
//        System.out.println("A before change = " + Arrays.deepToString(A));
        dfs:
        for (int row = 0; row < rows; row++) {
            for (int col = 0; col < cols; col++) {
                if (A[row][col] == 1) {
                    dfs(row, col, rows, cols, A);
                    break dfs;
                }
            }
        }
//        System.out.println("A after change = " + Arrays.deepToString(A));
        Queue<int[]> queue = new LinkedList<>();
        for (int row = 0; row < rows; row++) {
            for (int col = 0; col < cols; col++) {
                if (A[row][col] == 1) {
                    queue.add(new int[]{row, col});
                }
            }
        }
//        queue.forEach(val -> System.out.println(Arrays.toString(val)));
//        Perform a BFS to find the islands with -1.
        int level = 0;
        while (!queue.isEmpty()) {
            int size = queue.size();
            for (int i = 0; i < size; i++) {
                int[] poll = queue.poll();
//                Travel all the 4 directions that has zero's, and add to the list.
                for (int[] dir : dirs) {
                    int nr = poll[0] + dir[0], nc = poll[1] + dir[1];
                    if (nr < 0 || nr >= rows || nc < 0 || nc >= cols) continue;
                    if (A[nr][nc] == 1) continue;
                    if (A[nr][nc] == 2) return level;
                    if (A[nr][nc] == 0) {
                        A[nr][nc] = 1;
                        queue.add(new int[]{nr, nc});
                    }
                }
            }
            level++;
        }
        return level;
    }

    private static void dfs(int row, int col, int rows, int cols, int[][] a) {
        if (row < 0 || row >= rows || col < 0 || col >= cols || a[row][col] != 1) return;
        a[row][col] = 2;
        for (int[] dir : dirs)
            dfs(row + dir[0], col + dir[1], rows, cols, a);
    }
